"""
usando Requests
"""
# importando modulo Requests
import requests
from bs4 import BeautifulSoup

url = 'http://www.google.com'
r = requests.get(url)

print(r.status_code)
# 200
# 404
# não vai entrar no if com a url http://www.google.com/404
if r.status_code == requests.codes.ok:
    print('Retorno OK da página')

    soup = BeautifulSoup(r.text, 'html.parser')
    print(soup)

